function specialPerm(nums: number[]): number {
    const n = nums.length, mod = 1e9 + 7;
    nums.sort((a, b) => a - b);
    const dp: number[][] = Array.from({ length: n }, () => Array(1 << n).fill(0));
    const compatible: boolean[][] = Array.from({ length: n }, () => Array(n).fill(false));

    // Initialize compatibility based on divisibility
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            if (nums[i] % nums[j] === 0 || nums[j] % nums[i] === 0) {
                compatible[i][j] = true;
            }
        }
    }

    // Base cases: single elements
    for (let i = 0; i < n; i++) dp[i][1 << i] = 1;

    // Fill dp table
    for (let mask = 1; mask < (1 << n); mask++) {
        for (let i = 0; i < n; i++) {
            if ((mask & (1 << i)) === 0) continue; // Skip if i is not in the subset represented by mask
            for (let j = 0; j < n; j++) {
                if (i !== j && (mask & (1 << j)) !== 0 && compatible[i][j]) {
                    dp[i][mask] += dp[j][mask ^ (1 << i)];
                    dp[i][mask] %= mod;
                }
            }
        }
    }

    let result = 0;
    // Sum up all valid permutations ending with each element
    for (let i = 0; i < n; i++) {
        result += dp[i][(1 << n) - 1];
        result %= mod;
    }

    return result;
}
