public class Solution {
    public long MaxScore(int[] nums, int x) {
        int n = nums.Length;
        long[] prefixSums = new long[n + 1];
        for (int i = 0; i < n; i++) {
            prefixSums[i + 1] = prefixSums[i] + nums[i];
        }

        long maxScore = prefixSums[n];
        long minPenalty = long.MaxValue;

        // Two pointers technique to find minimum penalty for parity changes
        int j = 0;
        for (int i = 0; i < n; i++) {
            while (j < n && (j <= i || nums[j] % 2 == nums[j - 1] % 2)) {
                j++;
            }
            if (j < n) {
                long penalty = x * (long)(j - i);
                long score = prefixSums[j + 1] - prefixSums[i];
                minPenalty = Math.Min(minPenalty, penalty - score);
            }
        }

        return maxScore - (minPenalty == long.MaxValue ? 0 : minPenalty);
    }
}
