public class Solution {
    public long MaxScore(int[] nums, int x) {
        long totalScore = 0;
        int n = nums.Length;
        long maxScore = 0;
        for (int i = 0; i < n; i++) {
            totalScore += nums[i];
        }

        long currentScore = totalScore;
        int left = 0, right = n-1;
        while (left < n) {
            if ((nums[left] % 2 != nums[right] % 2)) {
                currentScore -= x;
            }
            maxScore = Math.Max(maxScore, currentScore);
            currentScore -= nums[left];
            left++;

            // Adjust right pointer to maintain parity change condition
            while (right > left && nums[right] % 2 != nums[left] % 2) {
                currentScore -= nums[right];
                right--;
            }
        }

        return maxScore;
    }
}
