#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();
        vector<long long> prefixSum(n + 1, 0);
        for (int i = 0; i < n; ++i) {
            prefixSum[i + 1] = prefixSum[i] + nums[i];
        }

        long long result = prefixSum[n]; // Start with the sum of all elements.
        long long currSum = 0;

        for (int i = 0, j = 0; i < n; ++i) {
            if (i > 0 && (nums[i] % 2 != nums[i - 1] % 2)) {
                currSum -= x; // Deduct x if parities differ.
            }
            currSum += nums[i]; // Add current number to currSum.

            // Maintain the sliding window [j, i] where the conditions are met.
            while (j < i && currSum < prefixSum[i + 1] - prefixSum[j]) {
                if (nums[j] % 2 != nums[j + 1] % 2) {
                    currSum += x; // Add x back if removing nums[j] fixes the parity issue.
                }
                currSum -= nums[j]; // Remove nums[j] from currSum.
                ++j;
            }

            result = max(result, currSum); // Update result with the maximum score found so far.
        }

        return result;
    }
};
