#include <vector>
using namespace std;

class Solution {
public:
    long long maxScore(vector<int>& nums, int x) {
        int n = nums.size();
        vector<long long> prefix(n + 1, 0);
        for (int i = 0; i < n; ++i) {
            prefix[i + 1] = prefix[i] + nums[i];
        }

        long long maxScore = prefix[1]; // Initial score with the first element only
        long long score, penalty;

        for (int i = 0; i < n; ++i) {
            score = prefix[i + 1]; // Score up to current index
            penalty = 0;
            for (int j = i + 1; j < n; ++j) {
                if ((nums[j] % 2) != (nums[j - 1] % 2)) {
                    penalty += x; // Apply penalty for parity change
                }
                score = max(score, prefix[i + 1] + prefix[j + 1] - prefix[i + 1] - penalty);
            }
            maxScore = max(maxScore, score);
        }

        return maxScore;
    }
};
