class Solution {
  int maxScore(List<int> nums, int x) {
    int n = nums.length, totalScore = nums[0], maxScore = nums[0];
    List<int> prefixSum = List.filled(n, 0);
    prefixSum[0] = nums[0];
    for (int i = 1; i < n; i++) {
      prefixSum[i] = prefixSum[i - 1] + nums[i];
      totalScore += nums[i];
    }
    int penalty = 0;
    for (int i = 0; i < n; i++) {
      if (i > 0 && nums[i] % 2 != nums[i - 1] % 2) {
        penalty += x;
      }
      int currentScore = prefixSum[i] - penalty;
      maxScore = maxScore > currentScore ? maxScore : currentScore;
    }
    return maxScore > totalScore - penalty ? maxScore : totalScore - penalty;
  }
}
