-spec max_score(Nums :: [integer()], X :: integer()) -> integer().
max_score(Nums, X) ->
    Lists = lists,
    {MaxScore, _} = Lists:foldl(fun (N, {Score, Last}) ->
                                     {NewScore, NewLast} = case (N rem 2) =:= (Last rem 2) of
                                                                true -> {Score + N, N};
                                                                false -> {Score + N - X, N}
                                                            end,
                                     {max(Score, NewScore), NewLast}
                                 end, {hd(Nums), hd(Nums)}, tl(Nums)),
    MaxScore.
