class Solution {
    public long maxScore(int[] nums, int x) {
        int n = nums.length;
        long maxScore = 0;
        long total = 0;
        long penalty = 0;
        long currentScore = 0;

        // Calculate total sum
        for (int num : nums) {
            total += num;
        }

        // Use sliding window to find the minimum penalty when not visiting some elements
        int left = 0, right = 0;
        while (right < n) {
            // Expand the window to the right
            if ((nums[left] % 2) != (nums[right] % 2)) {
                penalty += x; // Increment penalty if parities differ
            }
            currentScore += nums[right]; // Add current number to currentScore
            right++; // Move right pointer

            // Shrink the window from the left if necessary
            while ((right - left > 1 && penalty > total - currentScore) || (left < n && (nums[left] % 2) != (nums[left + 1] % 2) && right - left > 1)) {
                if ((nums[left] % 2) != (nums[left + 1] % 2)) {
                    penalty -= x; // Decrement penalty if parities differ
                }
                currentScore -= nums[left]; // Subtract the leftmost number from currentScore
                left++; // Move left pointer
            }

            // Update maxScore if current configuration is better
            if (total - penalty > maxScore) {
                maxScore = total - penalty;
            }
        }

        return maxScore;
    }
}
