class Solution {
    public long maxScore(int[] nums, int x) {
        long score = nums[0];
        long maxScore = score;
        int n = nums.length;
        int j = n; // Initialize j outside the bounds as we'll search from the end backwards.

        // First, try moving forwards and collecting scores until a parity change would cause a penalty.
        for (int i = 1; i < n; ++i) {
            if (nums[i] % 2 != nums[i - 1] % 2) {
                // If moving to nums[i] incurs a penalty, check if it's better to start removing elements from the end instead.
                while (j > i && (nums[j - 1] % 2 != nums[i] % 2 || j == n)) {
                    score += nums[--j];
                }
                if (j == i) break; // If j has met i, we've considered all elements we can without incurring a penalty.
                score -= x; // Apply the penalty for the parity change.
            }
            score += nums[i];
            maxScore = Math.max(maxScore, score);
        }

        // Reset score to include the possibility of not moving at all and only removing elements from the end to avoid penalties.
        score = nums[0];
        j = n;
        // Try to maximize score by only removing elements from the end to avoid penalties.
        for (int i = 1; i < n; ++i) {
            if (nums[i] % 2 != nums[i - 1] % 2) {
                score -= x; // Apply penalty for the parity change.
            }
            while (j > i && (nums[j - 1] % 2 != nums[i - 1] % 2 || j == n)) {
                score += nums[--j];
            }
            if (j == i) break; // If j has met i, all elements have been considered.
            score += nums[i];
            maxScore = Math.max(maxScore, score);
        }

        return maxScore;
    }
}
