class Solution {
    fun maxScore(nums: IntArray, x: Int): Long {
        val n = nums.size
        val dp = LongArray(n) { 0 }
        dp[0] = nums[0].toLong()

        for (i in 1 until n) {
            dp[i] = dp[i - 1] + nums[i]
            if (nums[i] % 2 != nums[i - 1] % 2) dp[i] -= x
        }

        var maxScore = dp[0]
        for (score in dp) {
            maxScore = maxOf(maxScore, score)
        }

        return maxScore
    }
}
