class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $x
     * @return Integer
     */
    function maxScore($nums, $x) {
        $n = count($nums);
        $dp = array_fill(0, $n + 1, 0); // DP array to hold the maximum score up to index i
        $dp[0] = $nums[0];
        for ($i = 1; $i < $n; $i++) {
            $dp[$i] = $dp[$i - 1] + $nums[$i]; // Accumulate scores
            if (($nums[$i] % 2) != ($nums[$i - 1] % 2)) { // Check if parity differs
                $dp[$i] -= $x; // Subtract x if parity differs
            }
        }

        // Max score after considering all positions
        $maxScore = $dp[$n - 1];
        for ($i = $n - 2; $i >= 0; $i--) {
            // Check if we can get a higher score by stopping at earlier position
            $maxScore = max($maxScore, $dp[$i]);
        }
        return $maxScore;
    }
}
