class Solution {
    /**
     * @param Integer[] $nums
     * @param Integer $x
     * @return Integer
     */
    function maxScore($nums, $x) {
        $n = count($nums);
        $maxScore = 0;
        $score = 0;
        $j = -1; // Start with -1 to include first element in the initial score calculation

        // Initial score from the first element
        $score += $nums[0];

        for ($i = 0; $i < $n; $i++) {
            // Move j to the right while ensuring the score does not exceed the limit
            while ($j + 1 < $n && ($i == $j + 1 || $score + $nums[$j + 1] - ($nums[$i] % 2 != $nums[$j + 1] % 2 ? $x : 0) <= $score)) {
                $j++;
                $score += $nums[$j] - ($nums[$i] % 2 != $nums[$j] % 2 ? $x : 0);
            }

            // Update max score
            $maxScore = max($maxScore, $score);

            // Prepare score for next iteration by subtracting the current element and penalty if moving to a different parity
            if ($i < $n - 1) {
                $score -= $nums[$i] + ($nums[$i] % 2 != $nums[$i + 1] % 2 ? $x : 0);
            }
        }

        return $maxScore;
    }
}
