from typing import List

class Solution:
    def maxScore(self, nums: List[int], x: int) -> int:
        n = len(nums)
        total = sum(nums)
        min_penalty = float('inf')
        i = 0
        current_penalty = 0
        for j in range(n):
            if nums[j] % 2 != nums[i] % 2:
                current_penalty += x
            while j - i + 1 > n // 2 or (j - i + 1 == n // 2 and current_penalty > x * (n // 2)):
                if nums[j] % 2 != nums[i] % 2:
                    current_penalty -= x
                i += 1
            if j - i + 1 == n // 2:
                min_penalty = min(min_penalty, current_penalty)
        if min_penalty == float('inf'):
            return total
        return total - min_penalty
