object Solution {
    def maxScore(nums: Array[Int], x: Int): Long = {
        val dp = Array.fill(nums.length + 1)(0L)
        for (i <- nums.indices) {
            dp(i + 1) = dp(i) + nums(i)
        }

        var maxScore = 0L
        var sum = 0L
        for (i <- nums.indices) {
            sum += nums(i)
            if (i > 0 && nums(i) % 2 != nums(i - 1) % 2) sum -= x
            maxScore = maxScore max sum
            var j = nums.length - 1
            var currentSum = sum
            while (j > i) {
                if (nums(j) % 2 != nums(j - 1) % 2) currentSum -= x
                currentSum += nums(j)
                maxScore = maxScore max currentSum
                j -= 1
            }
        }
        maxScore
    }
}
