public class Solution {
    public int NumberOfGoodSubarraySplits(int[] nums) {
        int totalCount = 0;
        int onesCount = 0;
        foreach (var num in nums) {
            if (num == 1) {
                onesCount++;
            }
        }
        if (onesCount == 0) return 0; // No way to split if there are no ones.

        int mod = 1000000007;
        int prefixOnes = 0, waysToSplit = 0;

        for (int i = 0; i < nums.Length; i++) {
            if (nums[i] == 1) {
                prefixOnes++;
            }
            // For each 1, count the splits where this 1 can be the ending of the first good subarray.
            if (prefixOnes > 0 && prefixOnes < onesCount) {
                waysToSplit = (waysToSplit + 1) % mod;
            }
        }

        return waysToSplit;
    }
}
