#include <vector>
using namespace std;

class Solution {
public:
    int numberOfGoodSubarraySplits(vector<int>& nums) {
        const int mod = 1e9 + 7;
        int onesCount = 0;
        for (int num : nums) {
            if (num == 1) ++onesCount;
        }

        if (onesCount == 0) return 0; // No good subarrays possible

        int currentOnes = 0, ways = 0;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] == 1) ++currentOnes;
            if (currentOnes == onesCount / 2) {
                if (onesCount % 2 == 0) {
                    ways = (ways + 1) % mod;
                } else {
                    if (i + 1 < nums.size() && nums[i + 1] == 1) {
                        ways = (ways + 1) % mod;
                    }
                }
            }
        }

        return ways;
    }
};
