#include <vector>
using namespace std;

class Solution {
public:
    int numberOfGoodSubarraySplits(vector<int>& nums) {
        const int mod = 1e9 + 7;
        vector<int> prefix(nums.size() + 1, 0);
        for (int i = 0; i < nums.size(); ++i) {
            prefix[i + 1] = prefix[i] + nums[i];
        }

        if (prefix.back() < 2) return 0; // Not enough ones to form at least two good subarrays

        long long ans = 0;
        for (int i = 1; i < prefix.size(); ++i) {
            if (nums[i - 1] == 1) {
                long long leftOnes = prefix[i] - 1; // Ones on the left
                long long rightOnes = prefix.back() - prefix[i]; // Ones on the right
                ans = (ans + min(leftOnes, rightOnes)) % mod;
            }
        }

        return ans;
    }
};
