#include <vector>
using namespace std;

class Solution {
public:
    int numberOfGoodSubarraySplits(vector<int>& nums) {
        long long ones = 0;
        for (int num : nums) {
            if (num == 1) ones++;
        }

        if (ones == 0) return 0; // No 1s means no good subarrays possible.

        long long count = 0, result = 0;
        const int mod = 1e9 + 7;
        for (int num : nums) {
            if (num == 1) count++;
            if (count == ones / 2 || count == (ones + 1) / 2) result++;
        }

        // If all 1s are in the middle, there's no way to split, so return 0.
        return ((ones & 1) == 0) ? (result - 1) % mod : result % mod;
    }
};
