int numberOfGoodSubarraySplits(int* nums, int numsSize){
    long long prefixSum = 0, ans = 0;
    int mod = 1e9 + 7;
    for (int i = 0; i < numsSize; ++i) {
        prefixSum += nums[i];
    }
    if (prefixSum < 2) return 0; // If there are less than 2 ones, cannot form good subarrays
    long long countOne = 0;
    for (int i = 0; i < numsSize - 1; ++i) { // Last element cannot start a new subarray
        if (nums[i] == 1) countOne++;
        if (countOne >= 1 && countOne < prefixSum) {
            ans = (ans + 1) % mod;
        }
    }
    return (int)ans;
}
