class Solution {
    public int numberOfGoodSubarraySplits(int[] nums) {
        final int MOD = 1000000007;
        int onesCount = 0;
        for (int num : nums) {
            if (num == 1) onesCount++;
        }

        if (onesCount == 0) return 0;

        long ways = 1;
        int currentOnes = 0;
        for (int i = 0; i < nums.length; i++) {
            if (nums[i] == 1) {
                currentOnes++;
                if (currentOnes < onesCount && currentOnes > 0) {
                    int zeros = 0;
                    while (nums[i + 1] == 0) {
                        zeros++;
                        i++;
                    }
                    ways = (ways * (zeros + 1)) % MOD;
                }
            }
        }

        return currentOnes == onesCount ? (int) ways : 0;
    }
}
