/**
 * @param {number[]} nums
 * @return {number}
 */
var numberOfGoodSubarraySplits = function(nums) {
    let totalOnes = nums.reduce((acc, val) => acc + val, 0);
    if (totalOnes === 0) return 0; // No 1s means no good subarray

    let mod = 1e9 + 7;
    let onesCount = 0, ways = 0;

    for (let i = 0; i < nums.length; i++) {
        if (nums[i] === 1) onesCount++;
        // For each 1 found, add the number of zeros before the next 1
        // to form a good subarray split at this point
        if (onesCount === 1 || onesCount === totalOnes) {
            let nextOneIndex = i + 1;
            while (nextOneIndex < nums.length && nums[nextOneIndex] !== 1) {
                nextOneIndex++;
            }
            ways = (ways + nextOneIndex - i) % mod;
        }
    }

    return ways % mod;
};
