class Solution {
    fun numberOfGoodSubarraySplits(nums: IntArray): Int {
        val mod = 1000000007
        val prefixSum = IntArray(nums.size + 1)
        for (i in nums.indices) {
            prefixSum[i + 1] = prefixSum[i] + nums[i]
        }
        if (prefixSum.last() < 2) return 0

        var result = 0L
        for (i in 1 until prefixSum.size) {
            if (prefixSum[i] == 1) {
                val left = i
                val right = prefixSum.size - i - 1
                result = (result + (left.toLong() * right.toLong()) % mod) % mod
            }
        }
        return result.toInt()
    }
}
