class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function numberOfGoodSubarraySplits($nums) {
        $totalOnes = array_sum($nums);
        if ($totalOnes <= 1) return $totalOnes;

        $mod = 1e9 + 7;
        $count = 0;
        $onesSoFar = 0;

        foreach ($nums as $num) {
            if ($num == 1) {
                $onesSoFar++;
            }
            if ($onesSoFar == $totalOnes - 1) {
                break;
            }
            if ($onesSoFar > 0 && $onesSoFar < $totalOnes) {
                $count++;
            }
        }

        return $count % $mod;
    }
}
